// Retrieve the raw data from the Modbus Read node

let rawData = msg.payload;

// Create a buffer to hold the 32-bit float (Big Endian format)
let buffer = Buffer.alloc(4);

// Write the high word (first register) into the buffer at position 0
buffer.writeUInt16BE(rawData[0], 0); // High word

// Write the low word (second register) into the buffer at position 2
buffer.writeUInt16BE(rawData[1], 2); // Low word

// Read the combined value as a 32-bit float
let floatValue = buffer.readFloatBE(0);

// Format the float value to 3 decimal places
let formattedValue = parseFloat(floatValue.toFixed(3));

// Assign the formatted value to msg.payload
//msg.payload = formattedValue;
msg.payload = {"adl200N_kW1":formattedValue};
//flow.set("currentNode1",msg.payload);
// Return the formatted value
flow.set("adl200n-kW1", formattedValue);
return msg;

