let doubleValue = buffer.readDoubleBE(0);

// Format the double value to 3 decimal places
let formattedValue = parseFloat(doubleValue.toFixed(3));

// Assign the formatted value to msg.payload
msg.payload = {"adl200N_kWh1": formattedValue};
flow.set("adl200n-kWh1", formattedValue);
return msg;

