const addressMap = {
    "Switch1": 0,
    "Switch2": 1,
    "Switch3": 2,
    "Switch4": 3,
    "Switch5": 4,
    "Switch6": 5
};

const switchNo = msg.payload.params.switchNo;
const value = msg.payload.params.value;
const modbusAddress = addressMap[switchNo];

if (modbusAddress !== undefined) {
    msg.payload = {
        //"value": [value ? 0xFF00 : 0x0000],
        value: value ? 1 : 0,
        "fc": 5,
        "unitid": "11",
        "quantity": "1",
        "address": modbusAddress
    };
    return msg;
} else {
    node.error("Error", msg);
    return null; // stop the flow
}


