    let buffer = Buffer.alloc(8);
    buffer.writeUInt16BE(reg[0], 0);
    buffer.writeUInt16BE(reg[1], 2);
    buffer.writeUInt16BE(reg[2], 4);
    buffer.writeUInt16BE(reg[3], 6);
    return buffer.readDoubleBE(0);
}

let totalEnergy = convertRegistersToDouble(registers);
