const switchNo = msg.payload.switchNo;
//const value = msg.payload.params.value;
const modbusAddress = addressMap[switchNo];

if (modbusAddress !== undefined) {
    // Create an array to hold both ON and OFF messages
    let messages = [];

    // Create the ON message
    let onMessage = {
        payload: {
            value: true, // ON state
            "fc": 5,
            "unitid": "1",
            "quantity": "1",
            "address": modbusAddress
        }
    };

    // Create the OFF message
    let offMessage = {
        payload: {
            value: false, // OFF state
            "fc": 5,
            "unitid": "1",
            "quantity": "1",
            "address": modbusAddress
        }
    };

    // Add ON message to the array
    messages.push(onMessage);

    // Add OFF message to the array after a 500ms delay
    setTimeout(() => {
        node.send(offMessage); // Send OFF message
    }, 2000);

    // Return the ON message immediately
    return onMessage;
} else {
    node.error("Error: Invalid switch number", msg);
    return null; // Stop the flow
}

//  flow context ()
let states = flow.get("states_withTimeStamp") || {
    "211A": 0,
    "211B": 0,
    "212A": 0,
    "212B": 0
};

let lastChangeTimes = flow.get("ON_OFF_lastChangeTimes") || {
    "211A": 0,
    "211B": 0,
    "212A": 0,
    "212B": 0
};

//  (15 )
const COOLDOWN_PERIOD = 15 * 60 * 1000; // 15  (900,000 )
const currentTime = Date.now();

// Initialize outputs
let output1 = null; //  ( ThingsBoard)
let output2 = null; //  (Cooldown  Error)

//  payload
if (!msg.payload || !msg.payload.params || !msg.payload.params.switchNo) {
    msg.payload = { "error": "Invalid input: switchNo is missing" };
    return [null, msg]; //  error  output2
}

// 
const switchNo = msg.payload.params.switchNo;
const parts = switchNo.split('_');

if (parts.length !== 2) {
    msg.payload = { "error": "Invalid switchNo format. Expected format: 'XXX_ON' or 'XXX_OFF'" };
    return [null, msg];
}

const baseSwitchNo = parts[0]; //  "211A"
const operation = parts[1]; //  "ON"  "OFF"

const elapsed = currentTime - (lastChangeTimes[baseSwitchNo] || 0);
const remainingMs = COOLDOWN_PERIOD - elapsed;
const remainingMinutes = Math.ceil(remainingMs / (60 * 1000));

//  payload  ThingsBoard
let telemetryData = {
    "switchNo": switchNo,
    "ready": elapsed >= COOLDOWN_PERIOD ? "ready" : "cooldown",
    "remaining_time": elapsed >= COOLDOWN_PERIOD ? 0 : remainingMinutes
};

if (operation === "ON") {
    if (states[baseSwitchNo] === 1) {
        msg.payload = { "warning": "It is already ON", "switchNo": switchNo };
        output2 = msg;
    } else if (elapsed < COOLDOWN_PERIOD) {
        msg.payload = { "warning": `Wait ${remainingMinutes} minutes before turning ON!`, "switchNo": switchNo };
        output2 = msg;
    } else {
        states[baseSwitchNo] = 1;
        lastChangeTimes[baseSwitchNo] = currentTime;
        telemetryData["Switch1"] = "ON";
        telemetryData["Last Change Time"] = new Date(lastChangeTimes[baseSwitchNo]).toISOString();
        output1 = { payload: telemetryData };
    }
} else if (operation === "OFF") {
    if (states[baseSwitchNo] === 0) {
        msg.payload = { "warning": "It is already OFF", "switchNo": switchNo };
        output2 = msg;
    } else if (elapsed < COOLDOWN_PERIOD) {
        msg.payload = { "warning": `Wait ${remainingMinutes} minutes before turning OFF!`, "switchNo": switchNo };
        output2 = msg;
    } else {
        states[baseSwitchNo] = 0;
        lastChangeTimes[baseSwitchNo] = currentTime;
        telemetryData["Switch1"] = "OFF";
        telemetryData["Last Change Time"] = new Date(lastChangeTimes[baseSwitchNo]).toISOString();
        output1 = { payload: telemetryData };
    }
} else {
    msg.payload = { "error": "Invalid operation. Expected 'ON' or 'OFF'" };
    return [null, msg];
}

//  flow
flow.set("states_withTimeStamp", states);
flow.set("ON_OFF_lastChangeTimes", lastChangeTimes);

// 
return [output1, output2];

