var g4 = msg["ComG4_voltage"];
var instructor = msg["Instructor_voltage"];
var normal = msg["NormalLine_voltage"];
var emergency = msg["EmergencyLine_voltage"];

function returnMsg() {
    return {
        msg: msg,
        metadata: metadata,
        msgType: msgType
    };
}

// === ‡∏Å‡∏£‡∏ì‡∏µ 3PhaseMeter ===
if (deviceName === "3PhaseMeter") {
    var hasAnyVoltage = msg.hasOwnProperty("averageVoltageKhw1") ||
                        msg.hasOwnProperty("averageVoltageKhw2") ||
                        msg.hasOwnProperty("averageVoltageKhw3") ||
                        msg.hasOwnProperty("averageVoltageKhw4");

    if (!hasAnyVoltage) return returnMsg();

    var allZero = 
        (voltage1 === 0 || voltage1 === undefined) &&
        (voltage2 === 0 || voltage2 === undefined) &&
        (voltage3 === 0 || voltage3 === undefined) &&
        (voltage4 === 0 || voltage4 === undefined);

    if (allZero) {
        msg.alarmType = "Power Outage";
        msg.alarmGroup = "AC";
        return returnMsg();
    }

    if (
        (voltage1 !== undefined && voltage1 < 210) ||
        (voltage2 !== undefined && voltage2 < 210) ||
        (voltage3 !== undefined && voltage3 < 210) ||
        (voltage4 !== undefined && voltage4 < 210)
    ) {
        msg.alarmType = "Undervoltage";
        msg.alarmGroup = "AC";
        return returnMsg();
    }

    if (
        (voltage1 !== undefined && voltage1 > 230) ||
        (voltage2 !== undefined && voltage2 > 230) ||
        (voltage3 !== undefined && voltage3 > 230) ||
        (voltage4 !== undefined && voltage4 > 230)
    ) {
        msg.alarmType = "Overvoltage";
        msg.alarmGroup = "AC";
        return returnMsg();
    }

    return returnMsg();
}


// === ‡∏Å‡∏£‡∏ì‡∏µ ADM ===
if (deviceName === "ADM") {
    var hasADMVoltage = msg.hasOwnProperty("ComG1_voltage") ||
                        msg.hasOwnProperty("ComG2_voltage") ||
                        msg.hasOwnProperty("ComG3_voltage") ||
                        msg.hasOwnProperty("ComG4_voltage") ||
                        msg.hasOwnProperty("Instructor_voltage") ||
                        msg.hasOwnProperty("NormalLine_voltage") ||
                        msg.hasOwnProperty("EmergencyLine_voltage");

    if (!hasADMVoltage) return returnMsg();

    // === ‡∏Å‡∏•‡∏∏‡πà‡∏° ComputerGroup ===
    var computerGroup = [
        { key: "ComG1_voltage", value: g1 },
        { key: "ComG2_voltage", value: g2 },
        { key: "ComG3_voltage", value: g3 },
        { key: "ComG4_voltage", value: g4 },
        { key: "Instructor_voltage", value: instructor }
    ];

    var computerOutage = computerGroup.some(function(v) {
        return v.value === 0;
    });

    var computerOver = computerGroup.some(function(v) {
        return v.value !== undefined && v.value > 240;
    });

    var computerUnder = computerGroup.some(function(v) {
        return v.value !== undefined && v.value < 210 && v.value !== 0;
    });

    if (computerOutage) {
        msg.alarmType = "Power Outage";
        msg.alarmGroup = "ComputerGroup";
        return returnMsg();
    }

    if (computerOver) {
        msg.alarmType = "Overvoltage";
        msg.alarmGroup = "ComputerGroup";
        return returnMsg();
    }

    if (computerUnder) {
        msg.alarmType = "Undervoltage";
        msg.alarmGroup = "ComputerGroup";
        return returnMsg();
    }

    // === ‡∏Å‡∏•‡∏∏‡πà‡∏° LightGroup ===
    var lightGroup = [
        { key: "NormalLine_voltage", value: normal },
        { key: "EmergencyLine_voltage", value: emergency }
    ];

    var lightOutage = lightGroup.some(function(v) {
        return v.value === 0;
    });

    var lightOver = lightGroup.some(function(v) {
        return v.value !== undefined && v.value > 240;
    });

    var lightUnder = lightGroup.some(function(v) {
        return v.value !== undefined && v.value < 210 && v.value !== 0;
    });

    if (lightOutage) {
        msg.alarmType = "Power Outage";
        msg.alarmGroup = "LightGroup";
        return returnMsg();
    }

    if (lightOver) {
        msg.alarmType = "Overvoltage";
        msg.alarmGroup = "LightGroup";
        return returnMsg();
    }

    if (lightUnder) {
        msg.alarmType = "Undervoltage";
        msg.alarmGroup = "LightGroup";
        return returnMsg();
    }

    return returnMsg();
}



// ‡πÑ‡∏°‡πà‡πÉ‡∏ä‡πà‡∏≠‡∏∏‡∏õ‡∏Å‡∏£‡∏ì‡πå‡∏ó‡∏µ‡πà‡∏™‡∏ô‡πÉ‡∏à ‚Üí return ‡∏õ‡∏Å‡∏ï‡∏¥
